  const handleSort = () => {
    setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
  };

  return (
    <table>
      <thead>
        <tr>
          <th 
            onClick={handleSort}
            className="cursor-pointer hover:bg-gray-100"
          >
            Name
            {sortSvg(sortOrder === 'desc')}
          </th>
          <th>Email</th>
        </tr>
      </thead>
      <tbody>
        {/* table rows */}
      </tbody>
    </table>
  );
};

