import { slashFilterSvg, emptyFilterSvg } from 'akeyless-client-commons/assets';

const TableControl = ({ filtersActive, theme }) => {
  const iconColor = theme === 'dark' ? '#fff' : '#333';

  return (
    <div className="table-controls">
      {filtersActive ? (
        <button className="clear-btn">
          {slashFilterSvg(true, iconColor)}
          Active Filters
        </button>
      ) : (
        <button className="set-filters-btn">
          {emptyFilterSvg(false)}
          Set Filters
        </button>
      )}
    </div>
  );
};

