  countries={['US', 'IL', 'GB', 'FR']}
  onEnter={() => handleSubmit()}
/>

incorrect_pattern.code:
// Incorrect - phone won't update
const phone = '+972545551234';
<InternationalPhonePicker phoneValue={phone} />
// Correct - provide both phoneValue and setPhoneValue
const [phone, setPhone] = useState('');
<InternationalPhonePicker phoneValue={phone} setPhoneValue={setPhone} />

