incorrect_pattern.code:
// Incorrect - ref not forwarded
const PhoneInput = (props) => <input {...props} />;
// Correct - use forwardRef for refs
const PhoneInput = forwardRef<HTMLInputElement, Props>((props, ref) => (
  <input ref={ref} {...props} />
));

