  options: [
    { label: 'Israel', value: 'IL' },
    { label: 'United States', value: 'US' }
  ],
  disabled: false
};

extensive_correct.code:
const countries = ['US', 'IL', 'GB'] as const;
const options: CountrySelectProps['options'] = countries.map(c => ({
  label: new Intl.DisplayNames(['en'], { type: 'region' }).of(c) || c,
  value: c as RPNInput.Country
}));
const props: CountrySelectProps = {
  value: 'IL' as RPNInput.Country,
  onChange: setCountry,
