minimal_correct.code:
import { ConfirmForm } from '@/components/forms';

const DeleteConfirm = () => {
  const handleDelete = async () => {
    await fetch('/api/delete', { method: 'DELETE' });
  };

  const handleCancel = () => {
    console.log('Cancelled');
  };

  return (
    <ConfirmForm
      headline="Are you sure you want to delete?"
      onV={handleDelete}
      onX={handleCancel}
    />
  );
};

extensive_correct.code:
import { ConfirmForm } from '@/components/forms';
import { Trash2, XCircle } from 'lucide-react';

const CustomConfirmDialog = () => {
  return (
    <ConfirmForm
      headline="Delete this record permanently?"
      onV={async () => {
        console.log('Deleting...');
        await new Promise(r => setTimeout(r, 1000));
        console.log('Deleted!');
      }}
      onX={() => alert('Cancelled')}
      direction="ltr"
      containerClassName="bg-red-50 border-2 border-red-300 rounded-lg"
      headlineClassName="text-red-700 text-xl font-bold"
      confirmElement={<Trash2 size={24} className="text-red-600" />}
      cancelElement={<XCircle size={24} className="text-gray-600" />}
      confirmButtonClassName="bg-red-600 hover:bg-red-700 p-2 rounded"
      cancelButtonClassName="bg-gray-400 hover:bg-gray-500 p-2 rounded"
    />
  );
};

