extensive_correct.code:
import { DatePicker2 } from '@/components/forms';

const AnalyticsDashboard = () => (
  <DatePicker2
    fromText="Report Start"
    toText="Report End"
    buttonText="Generate Report"
    direction="ltr"
    defaultFrom="2025-01-01"
    defaultTo="2025-01-31"
    formClassName="bg-gradient-to-r from-blue-50 to-indigo-50 p-6 rounded-xl shadow-sm"
    labelsClassName="font-semibold text-blue-900"
    calendarClassName="bg-white border-blue-200"
    selectedDayClassName="bg-blue-600 text-white rounded"
    todayDayClassName="border-2 border-blue-400"
    buttonClassName="bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white font-semibold"
    submit={async (e) => {
      const formData = new FormData(e.currentTarget);
      const startDate = formData.get('from');
      const endDate = formData.get('to');
      const response = await fetch('/api/reports/generate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ startDate, endDate })
      });
      const report = await response.json();
      console.log('Report generated:', report);
    }}
  />
);

