import { TableContext } from './Table';
import { useContext } from 'react';

function CustomTableCell() {
  const tableState = useContext(TableContext);
  if (!tableState) throw new Error('Must be used within Table');

  return (
    <div>
      Current search: {tableState.searchQuery}
      Total rows: {tableState.data.length}
    </div>
  );
}

