function TableStats() {
  const tableState = useContext(TableContext);
  if (!tableState) return null;

  const { dataToRender, filters, sortColumn, sortOrder, maxRows } = tableState;
  const { renderedData, filtered } = dataToRender;

  return (
    <div className="table-stats">
      <p>Total records: {tableState.data.length}</p>
      <p>Filtered records: {filtered.length}</p>
      <p>Displayed records: {renderedData.length}</p>
      {sortColumn !== null && (
        <p>Sorted by column {sortColumn} ({sortOrder})</p>
      )}
      {Object.keys(filters).length > 0 && (
        <p>Active filters: {Object.values(filters).flat().length} selected</p>
      )}
    </div>
  );
}

