// WRONG - new data array on every render
function BadTable() {
  return (
    <Table
      data={[
        { id: 1, name: 'Alice' },
        { id: 2, name: 'Bob' }
      ]}
      headers={['ID', 'Name']}
      keysToRender={['id', 'name']}
      filterableColumns={[
        { header: 'Name', dataKey: 'name' }
      ]}
    />
  );
  // New array and filterableColumns created each render, Table re-renders every time
}

// CORRECT - memoize props
function GoodTable() {
  const data = useMemo(() => [
    { id: 1, name: 'Alice' },
    { id: 2, name: 'Bob' }
  ], []);

  const filterableColumns = useMemo(() => [
    { header: 'Name', dataKey: 'name' }
  ], []);

  return (
    <Table
      data={data}
      headers={['ID', 'Name']}
      keysToRender={['id', 'name']}
      filterableColumns={filterableColumns}
    />
  );
  // Table only re-renders if data or filterableColumns content actually changes
}
