function TableActions({ item, canEdit }) {
  const [loading, setLoading] = useState(false);

  const handleSave = async () => {
    setLoading(true);
    try {
      await api.updateItem(item.id);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex gap-2">
      <TableButton
        type="edit"
        onClick={handleSave}
        disabled={loading || !canEdit}
        className="text-blue-500"
        buttonClassName="px-2 py-1"
        title={canEdit ? 'Save changes' : 'No permission'}
      />
      <TableButton
        type="add"
        onClick={() => console.log('Add new')}
        disabled={loading}
        title="Add related item"
      />
    </div>
  );
}

