minimal_correct.code:
import { badgeVariants } from 'akeyless-client-commons/components/ui/badge';

const customClasses = badgeVariants({ variant: 'destructive' });
// Returns all Tailwind classes for destructive badge variant

extensive_correct.code:
import { badgeVariants } from 'akeyless-client-commons/components/ui/badge';

const variantExamples = {
  default: badgeVariants({ variant: 'default' }),     // Primary blue
  secondary: badgeVariants({ variant: 'secondary' }),  // Secondary color
  destructive: badgeVariants({ variant: 'destructive' }), // Red warning
  outline: badgeVariants({ variant: 'outline' })       // Text only
};

import { Badge } from 'akeyless-client-commons/components/ui/badge';

const StatusDisplay = () => (
  <div className="flex gap-2">
    {/* Default blue badge */}
    <Badge variant="default">Active</Badge>

    {/* Secondary gray badge */}
    <Badge variant="secondary">Pending</Badge>

    {/* Destructive red badge */}
    <Badge variant="destructive">Error</Badge>

    {/* Outline text-only badge */}
    <Badge variant="outline">Info</Badge>

    {/* With custom className */}
    <Badge className="font-bold" variant="default">
      Important
    </Badge>
  </div>
);

