extensive_correct.code:
import { Label } from 'akeyless-client-commons/components/ui';
import { Input } from 'akeyless-client-commons/components/ui';

const FormWithOptional = ({ isDisabled }) => (
  <div className="space-y-4">
    {/* Regular required field */}
    <div>
      <Label htmlFor="username">Username *</Label>
      <Input id="username" placeholder="Enter username" />
    </div>

    {/* Optional field */}
    <div>
      <Label htmlFor="phone">Phone (optional)</Label>
      <Input id="phone" type="tel" placeholder="+1 (555) 000-0000" />
    </div>

    {/* Disabled field */}
    <div className="group" data-disabled={isDisabled}>
      <Label htmlFor="readonly">Read-only Field</Label>
      <Input id="readonly" disabled={isDisabled} value="Cannot edit" />
      {/* Label will automatically fade when input is disabled */}
    </div>
  </div>
);

