import { Popover, PopoverTrigger, PopoverContent } from 'akeyless-client-commons/components/ui';

const MyPopover = () => (
  <Popover>
    <PopoverTrigger asChild>
      <button>Open Menu</button>
    </PopoverTrigger>
    <PopoverContent>
      <p>Popover content goes here</p>
    </PopoverContent>
  </Popover>
);

