import { formatCarNumber } from './cars';
import type { Vehicle } from './types';

const vehicles: Vehicle[] = [
  { id: 1, plateNumber: '12345678', make: 'Honda' },
  { id: 2, plateNumber: '1234567', make: 'Toyota' },
  { id: 3, plateNumber: '234567', make: 'Mercedes' }
];

// Display formatted plates in UI
vehicles.forEach(v => {
  console.log(`${v.make}: ${formatCarNumber(v.plateNumber)}`);
  // Output:
  // Honda: 123-45-678
  // Toyota: 12-345-67
  // Mercedes: 23-45-67
});

