extensive_correct.code:
const perms = { dashboard: { read: true, write: true, delete: false } };
// Check if user has ANY of the permissions
const canRead = checkUserPermissions(perms, 'dashboard', ['read', 'write']);
// Check if user has ALL of the permissions
const canManage = checkUserPermissions(perms, 'dashboard', ['read', 'write', 'delete'], 'every');

incorrect_pattern.code:
