incorrect_pattern.code:
// These features will be ignored (no __):
const user = { features: ['dashboard', 'read', 'invalid_feature'] };
const perms = parsePermissions(user);
// Result: {} (empty)
// Correct format: module__action
const user = { features: ['dashboard__read'] };
const perms = parsePermissions(user);
// Result: { dashboard: { read: true } }

