extensive_correct.code:
function useUserPermissions(phoneNumber: string) {
  const [permissions, setPermissions] = useState<TObject<TObject<boolean>>>({});

  useEffect(() => {
    let unsubscribe: (() => void) | null = null;

    initializeUserPermissions({
      phoneNumber,
      firstTimeArray: ['dashboard', 'reports'],
      getUpdatePermissions: setPermissions
    }).then(({ unsubscribe: unsub }) => {
      unsubscribe = unsub;
    });

    return () => unsubscribe?.();
  }, [phoneNumber]);

  return permissions;
}

incorrect_pattern.code:
// Incorrect - memory leak from Firebase listener
initializeUserPermissions({
  email: 'user@example.com',
  firstTimeArray: [],
  getUpdatePermissions: setPerms
});

// Correct - unsubscribe when done
const { unsubscribe } = await initializeUserPermissions({...});
return () => unsubscribe(); // Call on cleanup
