incorrect_pattern.code:
// Incorrect - assumes UTC
const millis = timestamp_to_millis('2026-02-15 12:00:00', {
  fromFormat: 'YYYY-MM-DD HH:mm:ss'
});
// Correct - specify timezone
const millis = timestamp_to_millis('2026-02-15 12:00:00', {
  fromFormat: 'YYYY-MM-DD HH:mm:ss',
  tz: 'Asia/Jerusalem'
});

