import { useDocumentTitle } from 'hooks';

const HomePage = () => {
  useDocumentTitle('Home | My App');
  return <h1>Welcome</h1>;
};

import { useState } from 'react';
import { useSetUserCountry } from 'hooks';
import { CountryOptions } from 'akeyless-types-commons';

const App = () => {
  const [country, setCountry] = useState<CountryOptions>(CountryOptions.US);
  const [lang, setLang] = useState('en');

  useSetUserCountry(setCountry, (lang) => setLang(lang));

  return <div>User country: {country}, Language: {lang}</div>;
};
