incorrect_pattern.code:
// Incorrect - missing value property
const select: SelectElement = {
  type: 'select',
  options: [ { label: 'Option 1' } ] // Error: value missing
};
// Correct - both value and label required
const select: SelectElement = {
  type: 'select',
  options: [ { value: 'opt1', label: 'Option 1' } ]
};

