function useCustomState<T>(initialValue: T): [T, SetState<T>] {
  const [state, setState] = useState<T>(initialValue);

  const setStateTyped: SetState<T> = (updater) => {
    if (typeof updater === 'function') {
      setState(updater as (prev: T) => T);
    } else {
      setState(updater);
    }
  };

  return [state, setStateTyped];
}

