function getAppFeatures(app: AppName): Feature[] {
  switch (app) {
    case 'installer':
      return [new SetupFeature(), new ConfigureFeature()];
    case 'toolbox':
      return [new DebugFeature(), new ExportFeature()];
    case 'dashboard':
      return [new MonitorFeature(), new AnalyzeFeature()];
  }
}

function initiateLogin(option: LoginOption): Promise<User> {
  switch (option) {
    case 'google':
      return GoogleAuth.signin();
    case 'phone':
      return PhoneAuth.sendVerificationCode();
  }
}

const handleLogin = async (selected: LoginOption) => {
  try {
    const user = await initiateLogin(selected);
    setCurrentUser(user);
  } catch (error) {
    console.error(`${selected} login failed:`, error);
  }
};
