minimal_correct.code:
import { json_ok } from 'akeyless-server-commons';

app.get('/users', (req, res) => {
    const users = [{ id: 1, name: 'Alice' }];
    res.json(json_ok(users));
    // { success: true, data: [{ id: 1, name: 'Alice' }] }
});

import { json_failed } from 'akeyless-server-commons';

app.get('/users', (req, res) => {
    try {
        throw new Error('Database connection failed');
    } catch (error) {
        res.status(500).json(json_failed(error, 'Failed to fetch users'));
        // { success: false, error: 'Database connection failed', msg: 'Failed to fetch users' }
    }
});

