import { FeaturesClient } from 'akeyless-types-commons';

const CLIENT_ACTION_FEATURES: FeaturesClient[] = [
    FeaturesClient.cut_on_off,
    FeaturesClient.doors_open_close,
    FeaturesClient.vibration_on_off,
];

function getAvailableActions(features: string[]): FeaturesClient[] {
    return CLIENT_ACTION_FEATURES.filter(f => features.includes(f));
}

