import type { UserPermissionsObject } from 'akeyless-types-commons';
import { FeaturesDashboard } from 'akeyless-types-commons';

const perms: UserPermissionsObject = {
    dashboard: {
        client_admin: false,
        site_admin: true,
        capacity_admin: false,
        dashboard_user: true,
        super_admin: false,
    },
};

import type { userPermissionsObjectValue } from 'akeyless-types-commons';

type DashboardKey = userPermissionsObjectValue<'dashboard'>;
// DashboardKey = 'client_admin' | 'site_admin' | 'capacity_admin' | 'dashboard_user' | 'super_admin'

function hasDashboardFeature(
    perms: Record<DashboardKey, boolean>,
    key: DashboardKey
): boolean {
    return perms[key];
}
