import { DefaultClient } from 'akeyless-types-commons';

function resolveClient(clientId?: string): string {
    return clientId ?? DefaultClient.default;
}

import { BoardStatus } from 'akeyless-types-commons';
import type { Board } from 'akeyless-types-commons';

function getReadyBoards(boards: Board[]): Board[] {
    return boards.filter(b => b.status === BoardStatus.ReadyForInstallation);
}

import { EquipmentStatus } from 'akeyless-types-commons';
import type { Equipment } from 'akeyless-types-commons';

function isAvailable(equipment: Equipment): boolean {
    return equipment.status === EquipmentStatus.Ready;
}

import { EquipmentStatus, EquipmentStatusReason } from 'akeyless-types-commons';
import type { Equipment } from 'akeyless-types-commons';

function markBroken(equipment: Equipment): Partial<Equipment> {
    return {
        status: EquipmentStatus.Malfunction,
        status_reason: EquipmentStatusReason.Broken,
    };
}

import { UnitStatus } from 'akeyless-types-commons';
import type { Car } from 'akeyless-types-commons';

function isFullyInstalled(car: Car): boolean {
    return car.status === UnitStatus.Installed;
}

import { ClientStatus } from 'akeyless-types-commons';
import type { Client } from 'akeyless-types-commons';

function assertActiveClient(client: Client): void {
    if (client.status !== ClientStatus.Active) {
        throw new Error(`Client is not active: ${client.status}`);
    }
}

