import { SiteType } from 'akeyless-types-commons';
import type { Site } from 'akeyless-types-commons';

const INSTALLATION_TYPES: SiteType[] = [
    SiteType.installation_site_root,
    SiteType.installation_site,
];

function getInstallationSites(sites: Site[]): Site[] {
    return sites.filter(s => INSTALLATION_TYPES.includes(s.type));
}
